    (function(){

        var FA_TID_COOKIE = "fatid2011";
        var APPLY_BTN_CLASS = "fa-affiliate-btn";

        var createCookie = function(name, value, expireInDays) {
            var today = new Date(), path = '/', domain = '.forbes.com';
            today.setTime(today.getTime());
            if (expireInDays) {
                expireInDays = expireInDays * 1000 * 60 * 60 * 24;
            }
            var expireDate = new Date(today.getTime() + (expireInDays));
            document.cookie = name + "=" + escape(value) +
                    ((expireInDays) ? ";expires=" + expireDate.toGMTString() : "") +
                    ((domain) ? ";domain=" + domain : "") +
                    ((path) ? ";path=" + path : "");
        }
        var readCookie = function(name) {
            var start = document.cookie.indexOf(name + "=");
            var len = start + name.length + 1;
            if ((!start) && (name !== document.cookie.substring(0, name.length))) {
                return null;
            }
            if (start === -1) {
                return null;
            }
            var end = document.cookie.indexOf(";", len);
            if (end === -1) {
                end = document.cookie.length;
            }
            return unescape(document.cookie.substring(len, end));
        }
        var getQueryString = function ( field, url ) {
            var href = url ? url : window.location.href;
            var reg = new RegExp( '[?&]' + field + '=([^&#]*)', 'i' );
            var string = reg.exec(href);
            return string ? string[1] : null;
        }
        var addEvent = function(evnt, elem, func) {
            if (elem.addEventListener){
                elem.addEventListener(evnt,func,false);
            }else if (elem.attachEvent) { // IE DOM
                elem.attachEvent("on"+evnt, func);
            }
            else{
                elem[evnt] = func;
            }
        }
        var hasClass = function(element, className) {
            return (' ' + element.className + ' ').indexOf(' ' + className+ ' ') > -1;
        }

        var getTid = function(){
            var utm_campaign = getQueryString('utm_campaign');
            var utm_source = getQueryString('utm_source');
            var utm_medium = getQueryString('utm_medium');
            var utm_channel = getQueryString('utm_channel');

            if(!!utm_campaign && !!utm_source && !!utm_medium && !!utm_channel){
                var tid = utm_campaign+"|"+utm_source+"|"+utm_medium+"|"+utm_channel;
                createCookie(FA_TID_COOKIE, tid, 1);
                return tid;
            }
            return readCookie(FA_TID_COOKIE);
        }

        var handleAffiliate = function(){
            var tid = getTid();
            if(!!tid){
                var anchorTags = document.getElementsByTagName('a');
                for (var index = 0; index < anchorTags.length; index++) {
                    var anchorTag = anchorTags[index];
                    if(hasClass(anchorTag, APPLY_BTN_CLASS)){
                        var affiliateLink = anchorTag.href;
                        var symbol = (affiliateLink.indexOf("?") === -1) ? '?' : '&';
                        anchorTag.href = affiliateLink+symbol+"tid="+tid;
                    }
                }
            }
        }

        addEvent('DOMContentLoaded', window, handleAffiliate);

    })();

