        async function initializeTracking() {
            const userCookie = getCookie(COOKIE_NAME);

            if (userCookie && userCookie.length > MIN_COOKIE_LENGTH_FOR_USER_ID) {
                initializeWithExistingUser(userCookie);
            } else {
                try {
                    const newVisitorId = await generateVisitorId();
                    setCookieAndInitialize(newVisitorId);
                } catch (error) {
                    console.error("Error generating user ID:", error);
                }
            }
        }

        async function generateVisitorId() {
            const fp = await FingerprintJS.load();
            const { visitorId } = await fp.get();
            return "FA" + visitorId;
        }

        function initializeWithExistingUser(userId) {
            window.visitorId = userId;
            enableTrackingWithTid(userId);
        }

        function setCookieAndInitialize(visitorId) {
            setCookie(COOKIE_NAME, visitorId, COOKIE_EXPIRATION_DAYS, getTransferLinkDomain());
            window.visitorId = visitorId;
            enableTrackingWithTid(visitorId);
        }

        function enableTrackingWithTid(fpid) {
