            const r = "expires=" + n.toUTCString();
            document.cookie = e + "=" + encodeURIComponent(o) + ";" + r + ";path=/;domain=." + i;
        }

        function getTransferLinkDomain() {
            const e = new URL(window.location).hostname.split(".");
            return e
                .slice(0)
                .slice(-(4 === e.length ? 3 : 2))
                .join(".");
        }

        function getCookie(e) {
            let o = e + "=",
                t = decodeURIComponent(document.cookie).split(";");
