    function initDFPScrollhandler(callback, destroySlots) {
        _maddEventHandler(window, 'scroll', debounce(function () {
            var scrollTop = window.pageYOffset || document.documentElement.scrollTop;
            var direction = (scrollTop > previousScrollTop) ? 1 : -1; //downward scroll

            var large = 0;
            var largestVisibleElement = 0;
            for (var s = 0; s < sections.length; s++) {
                var section = sections[s];
                var percentage = getVisibilityForElement(section);
                if (percentage > 99) {
                    large = percentage;
                    largestVisibleElement = section;
                    break;
                }
                if (large < percentage) {
                    large = percentage;
                    largestVisibleElement = section;
                }
            }
            // console.log('lll ', largestVisibleElement, large, sections.length);
            if (largestVisibleElement) {
                var currentRowIndex = largestVisibleElement.getAttribute('data-index');
                var dataPageIndex = largestVisibleElement.getAttribute('data-page');
                var isDFPCalled = largestVisibleElement.getAttribute('data-dfp') || false;

                // Destroy old slots
                if (destroySlots === true) {
                    destroyOldSlots(currentRowIndex, dataPageIndex);
                }

                if (!!isDFPCalled) {
                    return;
                }

                if (callback && typeof (callback) === "function") {
                    callback(direction, currentRowIndex, dataPageIndex);
                }
                largestVisibleElement.setAttribute("data-dfp", true);

            }
            previousScrollTop = scrollTop <= 0 ? 0 : scrollTop;
        }, 250));
    }

    function destroyOldSlots(currentRowIndex, dataPageIndex) {
        var deleteOffset = 3;
        var articleIndex = dataPageIndex;
        var deleteRowIndex = currentRowIndex - deleteOffset;

        // To handle slides for previous article
        if (deleteRowIndex < 0 && articleIndex > 0) {
            // while(deleteRowIndex < 0 && articleIndex >= 0){}
            articleIndex = articleIndex - 1;
            if (slidesCountMap[articleIndex] >= 0) {
                deleteRowIndex = slidesCountMap[articleIndex] + deleteRowIndex;
            }
        }

        if (deleteRowIndex < 0) {
            return;
        }

        var batchNumber = articleIndex + "" + deleteRowIndex; //01
        if (allSlots[batchNumber] && allSlots[batchNumber].length != 0) {

            // console.log('-- Destroy --');
            // console.log('Row Index: ', deleteRowIndex);
            // console.log('BatchNumber -> ', batchNumber);

            googletag.cmd.push(function () {
                googletag.destroySlots(allSlots[batchNumber]);
            });
            allSlots[batchNumber] = [];

            var slotIndexMap = placeholderIndexMap[batchNumber];
            for (placeholderId in slotIndexMap) { // Eg: placeholderId = rec
                if (slotIndexMap.hasOwnProperty(placeholderId)) {
                    var indexArray = slotIndexMap[placeholderId]; //Eg: [1,2]
                    indexArray.forEach(function (index) {
                        var slotId = placeholderId + "-" + articleIndex + "-" + index; // eg: rec-0-1
                        defineSlotMap[slotId] = false;
                        // console.log('slotId -> ', slotId);
                    });
                }
            }

            var articleWrapper = document.getElementsByClassName('spa-article')[articleIndex];
            if (!!articleWrapper) {
                var articleRow = articleWrapper.getElementsByClassName('article-row')[deleteRowIndex];
                if (!!articleRow) {
                    articleRow.removeAttribute('data-dfp');
                }
            }
            // console.log('-- Destroy --');
        }
    }

    function loadSingleUnit(posArray, articleIndex, parentID) {
        var slotMetaDataArray = [];

        var parentEle = null;
        if (parentID) {
            parentEle = document.getElementById(parentID);
        } else {
            parentEle = document;
        }

        posArray.forEach(function (pos) {
            var placeholderClass = getcssClass(pos);
            var parentDiv = parentEle.getElementsByClassName(placeholderClass);

            if (!!parentDiv.length != 0) {
                //create div
                var placeholder = document.createElement("div");
                var slotId = pos + "-" + articleIndex; //rec-0
                placeholder.id = slotId;
                placeholder.className = 'placeholder';
                window.placeHolderMap[slotId] = true;
                parentDiv[0].appendChild(placeholder);

                var sizeArray = getSizeArray(pos);
                var meta = {
                    sizeArray: sizeArray,
                    slotId: slotId,
                    placeholderId: pos,
                    index: 1,
                    articleIndex: articleIndex,
                    pageIndex: 1,
                    unitPath: unitPath,
                    batch: articleIndex + "w"
                };
                slotMetaDataArray.push(meta);
            } else {
                console.error('placeholder not found', placeholderClass);
            }
        });
        if (slotMetaDataArray.length !== 0) {
            defineDFPSlotBatches(slotMetaDataArray);
        }
    }

