    var util = function(){

        /**
         * This function inserts a element after reference node
         * @param el
         * @param referenceNode
         */
        var insertAfter = function (el, referenceNode) {
            referenceNode.parentNode.insertBefore(el, referenceNode.nextSibling);
        };

        /**
         * This function inserts a element before reference node
         * @param el
         * @param referenceNode
         */
        var insertBefore = function(el, referenceNode) {
            referenceNode.parentNode.insertBefore(el, referenceNode);
        };

        /**
         * This function creates a element
         * @param el
         * @param attr
         * @returns {element}
         */
        function createElement(el,attr){
            el = el || 'div';
            attr = attr || {};
            var e = document.createElement(el);
            if( attr ){
                for(var p in attr){
                    e.setAttribute(p,attr[p]);
                }
            }
            return e;
        }

        function throttle(fn, delay) {
            var canCall = true;
            return function () {
                if (canCall) {
                    for (var _len = arguments.length, args = new Array(_len), _key = 0; _key < _len; _key++) {
                        args[_key] = arguments[_key];
                    }

                    fn.apply(null, args);
                    canCall = false;
                    setTimeout(function () {
                        canCall = true;
                    }, delay);
                }
            };
        };

        function debounce(fn, delay) {
            var timer;
            return function () {
                for (var _len = arguments.length, args = new Array(_len), _key = 0; _key < _len; _key++) {
                    args[_key] = arguments[_key];
                }

                if (timer) clearTimeout(timer);
                timer = setTimeout(function () {
                    fn.apply(null, args);
                }, delay);
            };
        };

        function $qs(selector){
            return document.querySelector(selector);
        }

        function $qsa(selector){
            return document.querySelectorAll(selector);
        }

        return {
            insertAfter:insertAfter,
            insertBefore:insertBefore,
            createElement:createElement,
            throttle:throttle,
            debounce:debounce,
            $qs:$qs,
        }
    }();

    /**
     * @returns {number}
     * @param calculationParameters
     *
     * First DFP section height: 1500px
     * Middle DFP section height: 1100px
     * Last min DFP section height: 800px
     *
     * Based on the above parameters calculate the number of DFP section for Right Rail
     */
    function getTotalSectionCountDesktop (calculationParameters) {
        var totalHeight = (calculationParameters.totalHeight - calculationParameters.heightDeduction);
        var count = 0;
        var remainingHeight = 0;

        if (totalHeight > RIGHT_RAIL_FIRST_DFP_SECTION_HEIGHT) {
          totalHeight -= RIGHT_RAIL_FIRST_DFP_SECTION_HEIGHT;
          count += 1;
        }

        var sectionCount = Math.floor(totalHeight / calculationParameters.sectionHeight);

        if (sectionCount) {
          count += sectionCount;
          remainingHeight = totalHeight - (sectionCount * RIGHT_RAIL_DFP_SECTION_HEIGHT);
        } else {
          remainingHeight = totalHeight;
        }


        if (remainingHeight >= RIGHT_RAIL_MIN_LAST_DFP_SECTION_HEIGHT) {
          count += 1;
        }

        return count < 0 ? 0 : count;
    }

    /**
     * @returns {number}
     * @param sectionHeight
     */
    function getTotalHeight(sectionHeight){
        var defaultHeight = sectionHeight || 2000;
        var sectionsParent;

        if (window.__articleMainContentSelector) {
            sectionsParent = document.querySelector(__articleMainContentSelector);
        } else {
            sectionsParent = document.querySelector(selectors.leftRailWrapper);
        }

        return ( !!sectionsParent ) ? sectionsParent.offsetHeight : defaultHeight ;
    }
    /**
     * @returns {number}
     */
    function getHeightDeduction(){
        var deductionHeight = 0;

        var rightRailContents = document.querySelectorAll(selectors.rightRailContent);

        if (rightRailContents && rightRailContents.length) {
            rightRailContents.forEach(function (ele) {
                if (!!ele) {
                    deductionHeight += ele.offsetHeight;
                }
            });
        }

        return deductionHeight;
    }

    /**
     * This function will generate placeholders
     */
    function generateRightRailPlaceholders(){
        var totalHeight = getTotalHeight(RIGHT_RAIL_DFP_SECTION_HEIGHT);
        var htDeduction = getHeightDeduction();
        var calculationParameters = {
            totalHeight: totalHeight,
            sectionHeight: RIGHT_RAIL_DFP_SECTION_HEIGHT,
            heightDeduction: htDeduction
        };
        var sectionCount = getTotalSectionCountDesktop(calculationParameters);

        if( !!sectionCount ){
            var html = '';
            var lastDfpSectionHeight = ( (totalHeight - htDeduction - RIGHT_RAIL_FIRST_DFP_SECTION_HEIGHT) - ( (sectionCount - 1) * RIGHT_RAIL_DFP_SECTION_HEIGHT ) ) + RIGHT_RAIL_DFP_SECTION_HEIGHT;
            for(var i=0; i < sectionCount ;i++){
                if ( i === 0 ){
                    html += '<div class="article-row" data-page="0" data-index="'+i+'" style="height:'+RIGHT_RAIL_FIRST_DFP_SECTION_HEIGHT+'px;"><div class="'+posData.rail.placeholder+' text-center sticky-item"></div></div>';
                } else if ( sectionCount - 1 === i ) {
                    html += '<div class="article-row" data-page="0" data-index="'+i+'" style="height:'+lastDfpSectionHeight+'px;"><div class="'+posData.rail.placeholder+' text-center sticky-item"></div></div>';
                }else{
                    html += '<div class="article-row" data-page="0" data-index="'+i+'" style="height:'+RIGHT_RAIL_DFP_SECTION_HEIGHT+'px;"><div class="'+posData.rail.placeholder+' text-center sticky-item"></div></div>';
                }
            }
            var rightRailDfp = document.querySelector(selectors.rightRailDfp);
            if(!!rightRailDfp){
                rightRailDfp.innerHTML = html;
            }
        }
    }

