    function generateIncontentPlaceholders(){
        var parentWrapper = document.querySelector(selectors.articleBody);
        var paras = parentWrapper.querySelectorAll('p');
        var dataIndex = 0;
        var initialLoad = true;
        var initialLoadCount = 0;
        var validParagraphCount = 0;
        var maxCharacterLength = 150;
        for(var i = 0 ; i < paras.length ;i++){
            var currentPara = paras[i];
            var text = currentPara.textContent || currentPara.innerText;
            var excludeCurrentPara = currentPara.getAttribute('data-ed-excluded-el');
            if( text.length > maxCharacterLength && excludeCurrentPara === null ){ validParagraphCount++; }
            if( ( initialLoad && validParagraphCount === 2 ) || validParagraphCount === 4 ){
                initialLoadCount++;
                if( initialLoadCount === 2 ){
                    initialLoad = false;
                }
                var wrapper = util.createElement('div',{
                    "class":'article-row',
                    "data-page":0,
                    "data-index":dataIndex,
                });
                wrapper.appendChild(
                    util.createElement('div',{class:posData.inlineArticle.placeholder})
                );
                util.insertAfter(
                    wrapper,
                    currentPara
                );
                dataIndex++;
                validParagraphCount = 0;
            }
        }
    }

